async function execute({...args}) {
  const { input, toolName, auth, axios, globals} = args;
  console.log("Tool::Script::"+ toolName +" with input:", input);

  if(!input.phone) {
    return JSON.stringify({
      success: false,
      message: "The phone number is required to use this tool"
    })
  }

  let response = {};
  try{
   const tokens = await auth.getTokens();
    if (!tokens.idToken) {
      console.error("Tool::Script::"+ toolName + " auth: ", "No access token available")
      throw new Error("There is an issue with the loyalty system, proceed without it");
    }
    response = await axios.get(globals.loyaltyAPIURL+`?phone=${input.phone}`, {
      headers: {
        "Authorization": `${tokens.idToken}`,
        "Content-Type": "application/json"
      }
    });   

    if(response.data.found){
      return JSON.stringify({ 
        result: "success",
        userInfo: {
          name: response.data.customer.name,
          email: response.data.customer.email,
          points: response.data.customer.loyaltyPoints
        }
      });
    }else{
      throw new Error("There was no loyalty information associated with the phone number provided");  
    }
  }catch(e){
    return JSON.stringify({
      result: "error",
      error_message: e.message
    })
  }

}

async function execute({...args}) {
  const { agentTriggered, axios, components, auth, toolName, globals, utils } = args;
  //console.log("Tool::Script::"+ toolName +" with input:", args);

  try {
    // Show loading notification
    components.ui.showNotification("Retriving menu...", "info");
    // Get Cognito access token for API Gateway authentication
    const tokens = await auth.getTokens();
    if (!tokens.idToken) {
      throw new Error("No access token available");
    }

    console.log("Tool::Script::"+ toolName +" idToken:", tokens.idToken);

    // Make authenticated API call to your API Gateway
    const menu = utils.storage.getData('cachedMenu');
    var response = {};
    if(!menu){
      response = await axios.get(globals.menuAPIURL, {
        headers: {
          "Authorization": `${tokens.idToken}`,
          "Content-Type": "application/json"
        }
      });
      utils.storage.setData('cachedMenu', JSON.stringify(response), 2);
      console.log("Tool::Script::"+ toolName +" storing menu in cache");
    }else{
      console.log("Tool::Script::"+ toolName +" get menu from cache");
      response = JSON.parse(menu);
    }


    var items = [];
    if(response.data.items.length){
      items = [...new Set(response.data.items.map((item) => {
        return {
          ...item,
          available: true,
        }
      }))]
    }

    // Avoid reloading elements in the view
    // Only if is not triggered by the agent, the menu will be cleared out
    if(!agentTriggered){
      // Clear existing menu and add sample items
      components.menu.clearMenu();
      components.menu.addItems(items);
      components.menu.setActiveCategory("All Items")

      // Show success notification
      components.ui.showNotification(
        `Loaded ${items.length} menu items successfully!`, 
        "success"
      );
    }else{
      // Show success notification
      components.ui.showNotification(
        `The agent got the updated menu!`, 
        "success"
      );
    }

    const result = JSON.stringify({
      success: true,
      itemsLoaded: items.length,
      categories: components.menu.getAllCategories(),
      menu: [... new Set(items.map((item) => {
        // Avoid image urls to be returned to the model for token optimization.
        return {
          ...item,
          image: null
        };
      }))]
    });

    return result;

  } catch (error) {
    components.ui.showNotification("Failed to load sample menu", "error");
    console.log("Tool::Script::"+toolName+" error:", error);
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

