async function execute({...args}) {
  const { input, components, toolName, utils, auth, axios, globals } = args;
  console.log("Tool::Script::" + toolName + " executed with input:", JSON.stringify(input, null, 2));

  try {
    const roomNumber = input.roomNumber;

    // Validate room number format
    if (!roomNumber || !/^\d{3,4}$/.test(roomNumber)) {
      components.ui.showNotification("Invalid room number format", "error");
      return JSON.stringify({
        success: false,
        error: "Room number must be 3-4 digits (e.g., 101, 1205)"
      });
    }

    // Store room number in session
    utils.storage.setData('guestRoomNumber', roomNumber);

    // Fetch room booking information
    let guestInfo = null;
    let loyaltyInfo = null;

    try {
      const tokens = await auth.getTokens();
      console.log("Tool::Script::" + toolName + " tokens available:", tokens ? "yes" : "no");
      console.log("Tool::Script::" + toolName + " idToken exists:", tokens.idToken ? "yes" : "no");
      console.log("Tool::Script::" + toolName + " roomBookingAPIURL:", globals.roomBookingAPIURL);

      if (tokens.idToken && globals.roomBookingAPIURL) {
        console.log("Tool::Script::" + toolName + " fetching room booking for:", roomNumber);

        const bookingResponse = await axios.get(`${globals.roomBookingAPIURL}?roomNumber=${roomNumber}`, {
          headers: {
            "Authorization": `${tokens.idToken}`,
            "Content-Type": "application/json"
          }
        });

        console.log("Tool::Script::" + toolName + " booking API response status:", bookingResponse.status);
        console.log("Tool::Script::" + toolName + " booking API response data:", JSON.stringify(bookingResponse.data, null, 2));

        if (bookingResponse.data.found) {
          guestInfo = bookingResponse.data.booking;
          console.log("Tool::Script::" + toolName + " found guest:", guestInfo.guestName);
          console.log("Tool::Script::" + toolName + " guest phone:", guestInfo.phone);
          console.log("Tool::Script::" + toolName + " guest customerId:", guestInfo.customerId);

          // Store guest info in session
          utils.storage.setData('guestInfo', JSON.stringify(guestInfo));

          // Fetch loyalty information using phone (primary) or customerId (fallback)
          if (globals.loyaltyAPIURL) {
            let loyaltyURL = null;
            let lookupMethod = null;

            if (guestInfo.phone) {
              loyaltyURL = `${globals.loyaltyAPIURL}?phone=${encodeURIComponent(guestInfo.phone)}`;
              lookupMethod = "phone";
            } else if (guestInfo.customerId) {
              loyaltyURL = `${globals.loyaltyAPIURL}?customerId=${guestInfo.customerId}`;
              lookupMethod = "customerId";
            }

            if (loyaltyURL) {
              console.log("Tool::Script::" + toolName + " fetching loyalty using", lookupMethod + ":", loyaltyURL);

              const loyaltyResponse = await axios.get(loyaltyURL, {
                headers: {
                  "Authorization": `${tokens.idToken}`,
                  "Content-Type": "application/json"
                }
              });

              console.log("Tool::Script::" + toolName + " loyalty API response status:", loyaltyResponse.status);
              console.log("Tool::Script::" + toolName + " loyalty API response data:", JSON.stringify(loyaltyResponse.data, null, 2));

              if (loyaltyResponse.data.found) {
                loyaltyInfo = loyaltyResponse.data.customer;
                console.log("Tool::Script::" + toolName + " found loyalty status:", loyaltyInfo.loyaltyStatus);
                console.log("Tool::Script::" + toolName + " loyalty points:", loyaltyInfo.loyaltyPoints);
                console.log("Tool::Script::" + toolName + " customer name:", loyaltyInfo.name);

                // Store loyalty info in session
                utils.storage.setData('loyaltyInfo', JSON.stringify(loyaltyInfo));
              } else {
                console.log("Tool::Script::" + toolName + " loyalty not found for", lookupMethod);
              }
            } else {
              console.log("Tool::Script::" + toolName + " no phone or customerId available for loyalty lookup");
            }
          } else {
            console.log("Tool::Script::" + toolName + " loyaltyAPIURL not configured");
          }
        } else {
          console.log("Tool::Script::" + toolName + " room booking not found for room:", roomNumber);
        }
      } else {
        console.log("Tool::Script::" + toolName + " missing tokens or roomBookingAPIURL");
      }
    } catch (apiError) {
      console.error("Tool::Script::" + toolName + " API error:", apiError);
      console.error("Tool::Script::" + toolName + " API error message:", apiError.message);
      console.error("Tool::Script::" + toolName + " API error response:", apiError.response?.data);
      console.error("Tool::Script::" + toolName + " API error status:", apiError.response?.status);
    }

    // Show success notification
    if (guestInfo && loyaltyInfo) {
      components.ui.showNotification(
        `Welcome back, ${guestInfo.guestName}! ${loyaltyInfo.loyaltyStatus} member confirmed for Room ${roomNumber}`, 
        "success"
      );
    } else if (guestInfo) {
      components.ui.showNotification(
        `Room ${roomNumber} confirmed for ${guestInfo.guestName}`, 
        "success"
      );
    } else {
      components.ui.showNotification(
        `Room ${roomNumber} confirmed`, 
        "success"
      );
    }

    console.log("Tool::Script::" + toolName + " final result - guestInfo:", guestInfo ? "found" : "not found");
    console.log("Tool::Script::" + toolName + " final result - loyaltyInfo:", loyaltyInfo ? "found" : "not found");

    return JSON.stringify({
      success: true,
      roomNumber: roomNumber,
      guestInfo: guestInfo,
      loyaltyInfo: loyaltyInfo,
      message: guestInfo ? 
        `Room ${roomNumber} confirmed for ${guestInfo.guestName}` : 
        `Room ${roomNumber} confirmed`
    });

  } catch (error) {
    console.error("Tool::Script::" + toolName + " error:", error);
    components.ui.showNotification("Failed to validate room number", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

