tools.script:
async function execute({...args}) {
  const { input, components, toolName, utils, auth, axios, globals } = args;
  console.log("Tool::Script::"+toolName+" executed with input:", JSON.stringify(input, null, 2));

  // Helper function to check if two customization arrays are the same
  const customizationsMatch = (customizations1, customizations2) => {
    if (!customizations1 && !customizations2) return true;
    if (!customizations1 || !customizations2) return false;
    if (customizations1.length !== customizations2.length) return false;

    // Sort both arrays by id for comparison
    const sorted1 = [...customizations1].sort((a, b) => a.id.localeCompare(b.id));
    const sorted2 = [...customizations2].sort((a, b) => a.id.localeCompare(b.id));

    return sorted1.every((custom1, index) => custom1.id === sorted2[index].id);
  };

  // Helper function to find existing cart item with same menuItemId and customizations
  const findExistingCartItem = (menuItemId, customizations) => {
    const currentCart = components.cart.getOrderSummary();
    console.log("Tool::Script::"+toolName+" Current Order Summary:", currentCart);
    console.log("Tool::Script::"+toolName+" Requested MenuItemID:", menuItemId);
    console.log("Tool::Script::"+toolName+" Requested customizations:", customizations);

    // Log each cart item for debugging (not the cart summary itself)
    if (currentCart.items && currentCart.items.length > 0) {
      currentCart.items.forEach((cartItem, index) => {
        console.log("Tool::Script::"+toolName+" Cart Item", index, "MenuItemID:", cartItem.menuItemId);
        console.log("Tool::Script::"+toolName+" Cart Item", index, "customizations:", cartItem.customizations);
      });
    } else {
      console.log("Tool::Script::"+toolName+" Cart is empty");
    }


    return currentCart.items.find(cartItem => 
      cartItem.menuItemId === menuItemId && 
      customizationsMatch(cartItem.customizations, customizations)
    );
  };

  try {
    if(input.items.length === 0){
      throw("No items passed to the tool, at least one item is needed");
    }

    const items = input.items;
    const itemsToAdd = [];
    const itemsToUpdate = [];
    const processedItems = [];

    // Initialize cartResult and comboApplied at the top
    let cartResult = null;
    let comboApplied = null;

    // Adding items in the array one by one to the cart
    for(const element of items){
      // Search for the item by id
      console.log("Tool::Script::"+toolName+" getItemById:", element.itemId);
      let item = components.menu.getItemById(element.itemId);
      console.log("Tool::Script::"+toolName+" Retrieved Item :", item);

      //If the item could be found, we will try to search for it, in case the agent made a mistake while providing the id
      if(!item){
        console.log("Tool::Script::"+toolName+" no item found using id:", element.itemId);
        components.ui.showNotification(`Searching for item "${element.itemId}" `, "info");

        const searchResult = components.menu.searchItems(element.itemId);

        console.log("Tool::Script::"+toolName+" product found for cart", searchResult);

        if(searchResult.length > 1){
          return JSON.stringify({
            success: true,
            message: "Confirm with the customer which menu item he is referring to",
            products: searchResult
          });
        }else{
          console.log("Tool::Script::"+toolName+" product found for cart", searchResult);
          item = searchResult[0]
        }
      }

      // If the item was not found despite the effort of searching for it
      if (!item) {
        components.ui.showNotification(`Item "${element.itemId}" not found`, "error");
        return JSON.stringify({
          success: false,
          error: `Item '${element.itemId}' not found in menu`
        });
      }

      // ========================================
      // IMPROVED CUSTOMIZATION VALIDATION
      // ========================================

      const customizations = [];

      try {
        if (Array.isArray(element.customizations) && element.customizations.length > 0) {
          console.log("Tool::Script::"+toolName+" validating customizations for:", item.name);
          console.log("Tool::Script::"+toolName+" requested customizations:", element.customizations);
          console.log("Tool::Script::"+toolName+" available customizations:", item.availableCustomizations);

          // Validate each customization individually
          for (const cRequested of element.customizations) {
            const foundCustomization = item.availableCustomizations.find(cAvailable => 
              cRequested.id === cAvailable.id
            );

            if (!foundCustomization) {
              // Invalid customization - provide meaningful feedback
              const availableCustomizationNames = item.availableCustomizations.length > 0 
                ? item.availableCustomizations.map(c => c.name).join(', ')
                : 'none';

              const errorMessage = item.availableCustomizations.length > 0
                ? `"${cRequested.id}" customization is not available for ${item.name}. Available customizations: ${availableCustomizationNames}`
                : `${item.name} does not have any customizations available`;

              console.log("Tool::Script::"+toolName+" CUSTOMIZATION ERROR:", errorMessage);

              // Show user notification
              components.ui.showNotification(
                `Invalid customization: ${cRequested.id} not available for ${item.name}`, 
                "error"
              );

              return JSON.stringify({
                success: false,
                error: errorMessage,
                itemName: item.name,
                requestedCustomization: cRequested.id,
                availableCustomizations: item.availableCustomizations.map(c => ({
                  id: c.id,
                  name: c.name,
                  price: c.price,
                  isRemoval: c.isRemoval || false
                })),
                suggestion: item.availableCustomizations.length > 0 
                  ? `Please choose from: ${availableCustomizationNames}`
                  : `${item.name} cannot be customized`
              });
            }

            // Valid customization - add it
            customizations.push(foundCustomization);
            console.log("Tool::Script::"+toolName+" valid customization added:", foundCustomization.name);
          }
        }

        console.log("Tool::Script::"+toolName+" final validated customizations:", customizations);

      } catch(e) {
        console.error("Tool::Script::"+toolName+" customization validation error:", e);
        return JSON.stringify({
          success: false,
          error: `Error processing customizations for ${item.name}: ${e.message}`
        });
      }

      // If the item becomes unavailable, we need to tell that to the agent
      if (!item.available) {
        return JSON.stringify({
          success: false,
          error: `Item id "${element.itemId}" is not available right now, it could be out of stock`
        });
      }

      // Check if this item already exists in cart with same customizations
      console.log("Tool::Script::"+toolName+" checking for existing cart item with menuItemId:", item.id, "and customizations:", customizations);
      const existingCartItem = findExistingCartItem(String(item.id), customizations);

      if (existingCartItem) {
        // Item exists - update quantity instead of adding new
        const newQuantity = existingCartItem.quantity + (element.quantity || 1);
        console.log("Tool::Script::"+toolName+" found existing item:", existingCartItem.name, "current qty:", existingCartItem.quantity, "adding:", element.quantity, "new total:", newQuantity);

        itemsToUpdate.push({
          cartItemId: existingCartItem.id,
          itemName: existingCartItem.name,
          oldQuantity: existingCartItem.quantity,
          addedQuantity: element.quantity || 1,
          newQuantity: newQuantity
        });

        // Use updateQuantity to merge
        console.log("Tool::Script::"+toolName+" calling updateQuantity for existing item");
        const updateResult = components.cart.updateQuantity(existingCartItem.id, newQuantity);
        cartResult = { summary: updateResult };

        // Show success notification for merge
        components.ui.showNotification(
          `Added ${element.quantity || 1}x ${item.name} to existing cart item (${newQuantity} total)`, 
          "success"
        );

      } else {
        // Item doesn't exist - add as new item
        console.log("Tool::Script::"+toolName+" no existing item found, adding as new item");

        item.menuItemId = String(item.id);
        item.quantity = element.quantity || 1;
        item.customizations = customizations; // Now guaranteed to be valid
        itemsToAdd.push(item);

        // Show success notification for new item
        const customizationText = customizations.length > 0 
          ? ` with ${customizations.map(c => c.name).join(', ')}`
          : '';

        components.ui.showNotification(
          `Added ${item.quantity}x ${item.name}${customizationText} to cart`, 
          "success"
        );
      }

      // Highlight the item in the menu
      components.menu.highlightItem(item.id, 3000);

      processedItems.push({
        itemId: element.itemId,
        itemName: item.name,
        quantity: element.quantity || 1,
        action: existingCartItem ? 'merged' : 'added',
        customizations: customizations
      });   
    }

    // Add new items to cart (if any) and capture combo information
    if (itemsToAdd.length > 0) {
      console.log("Tool::Script::"+toolName+" adding", itemsToAdd.length, "new items to cart");
      cartResult = components.cart.bulkAddToCart(itemsToAdd);
      console.log("Tool::Script::"+toolName+" bulkAddToCart result:", JSON.stringify(cartResult, null, 2));

      // Check if combo was applied
      if (cartResult && cartResult.comboApplied) {
        comboApplied = cartResult.comboApplied;
        console.log("Tool::Script::"+toolName+" COMBO DETECTED:", comboApplied.comboName, "savings:", comboApplied.savings);

        // Show combo notification
        components.ui.showNotification(
          `ðŸŽ‰ ${comboApplied.comboName} applied! Saved $${comboApplied.savings.toFixed(2)}`, 
          "success"
        );
      }
    }

    // Ensure cartResult is always set
    if (!cartResult) {
      cartResult = { summary: components.cart.getOrderSummary() };
    }

    console.log("Tool::Script::"+toolName+" final cart result:", JSON.stringify(cartResult, null, 2));

    // Sending cart item to the API for synchronization (Drive-thru logic with roomNumber)
    const tokens = await auth.getTokens();
    const payload = {
      roomNumber: utils.storage.getData('guestRoomNumber'),
      loyaltyId: input.loyaltyId || "null",
      cartSummary: JSON.stringify(cartResult.summary).replace(/"/g, '\\"')
    }
    console.log("Tool::Script::"+toolName+" syncing with API:", JSON.stringify(payload, null, 2));

    // Fire-and-forget with error handling
    if (tokens.idToken && globals.cartAPIURL) {
      axios.post(globals.cartAPIURL, payload, {
        headers: {
          "Authorization": `${tokens.idToken}`,
          "Content-Type": "application/json"
        }
      }).then(response => {
        console.log("Tool::Script::"+toolName+" cart synced successfully:", response.data);
      }).catch(syncError => {
        console.error("Tool::Script::"+toolName+" cart sync error:", syncError);
        console.error("Tool::Script::"+toolName+" error details:", syncError.response?.data);
        // Don't fail the whole operation if sync fails
      });
    } else {
      console.log("Tool::Script::"+toolName+" missing tokens or cartAPIURL");
    }

    // Include combo information in the result
    const result = JSON.stringify({
      success: true,
      processedItems: processedItems,
      newItems: cartResult ? cartResult.addedItems : [],
      updatedItems: itemsToUpdate,
      cartSummary: cartResult.summary,
      ...(comboApplied && { comboApplied })
    }); 

    console.log("Tool::Script::"+toolName+" executed with output:", result);

    return result;

  } catch (error) {
    console.error("Tool::Script::"+toolName+" error:", error);
    components.ui.showNotification("Failed to add item to cart", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

