async function execute({...args}) {
  const { input, toolName, components, auth, axios, globals, utils } = args;

  console.log("Tool::Script::"+toolName+" executed input:", input);

  try {
    const cartItem = components.cart.getCartItem(input.cartItemId);
    if (!cartItem) {
      return JSON.stringify({
        success: false,
        error: "Cart item not found"
      });
    }

    console.log("Tool::Script::"+toolName+" cart item:", cartItem);

    const menuItem = components.menu.getItemById(cartItem.menuItemId);
    if (!menuItem) {
      return JSON.stringify({
        success: false,
        error: "Menu item not found"
      });
    }

    console.log("Tool::Script::"+toolName+" item to update:", menuItem);

    const customizationsToAdd = [];
    const customizationsToReplace = []; // NEW: Track replacements
    const alreadyExists = [];
    const notAvailable = [];

    if (Array.isArray(input.customizations) && input.customizations.length > 0) {
      for (const cRequested of input.customizations) {
        // Find the customization in menu
        const availableCustomization = menuItem.availableCustomizations.find(cAvailable => 
          cRequested.id === cAvailable.id
        );

        if (!availableCustomization) {
          notAvailable.push(cRequested.id);
          continue;
        }

        // Check if customization already exists on cart item
        const existingCustomization = cartItem.customizations.find(existing => 
          existing.id === cRequested.id
        );

        if (existingCustomization) {
          alreadyExists.push(availableCustomization.name);
          console.log("Tool::Script::"+toolName+" customization already exists:", availableCustomization.name);
          continue;
        }

        // NEW: Check for mutually exclusive customizations
        const conflictingCustomization = cartItem.customizations.find(existing => {
          const existingId = existing.id.toLowerCase();
          const newId = availableCustomization.id.toLowerCase();

          // Linen types are mutually exclusive
          if ((existingId.includes('linen-') || existingId.includes('linens-')) && 
              (newId.includes('linen-') || newId.includes('linens-'))) {
            return true;
          }

          // Towel types are mutually exclusive  
          if ((existingId.includes('towel-') || existingId.includes('towels-')) && 
              (newId.includes('towel-') || newId.includes('towels-'))) {
            return true;
          }

          return false;
        });

        if (conflictingCustomization) {
          // This is a replacement scenario
          customizationsToReplace.push({
            oldCustomization: conflictingCustomization,
            newCustomization: availableCustomization
          });
          console.log("Tool::Script::"+toolName+" will replace:", conflictingCustomization.name, "with:", availableCustomization.name);
        } else {
          // This is a new addition
          customizationsToAdd.push(availableCustomization);
          console.log("Tool::Script::"+toolName+" will add customization:", availableCustomization.name);
        }
      }
    }

    // Handle errors
    if (notAvailable.length > 0) {
      return JSON.stringify({
        success: false,
        error: `Customizations not available: ${notAvailable.join(', ')}`,
        availableCustomizations: menuItem.availableCustomizations.map(c => ({
          id: c.id,
          name: c.name,
          price: c.price
        }))
      });
    }

    let item = cartItem;
    const addedCustomizations = [];
    const replacedCustomizations = []; // NEW: Track what was replaced

    // NEW: Handle replacements first
    for (const replacement of customizationsToReplace) {
      // Remove old customization
      await components.cart.removeCustomizationFromCartItem(input.cartItemId, replacement.oldCustomization.id);
      // Add new customization
      item = await components.cart.addCustomizationToCartItem(input.cartItemId, replacement.newCustomization);

      replacedCustomizations.push({
        old: replacement.oldCustomization.name,
        new: replacement.newCustomization.name
      });
      console.log("Tool::Script::"+toolName+" replaced:", replacement.oldCustomization.name, "with:", replacement.newCustomization.name);
    }

    // Add only new customizations
    for (const customization of customizationsToAdd) {
      item = await components.cart.addCustomizationToCartItem(input.cartItemId, customization);
      addedCustomizations.push(customization.name);
      console.log("Tool::Script::"+toolName+" added customization:", customization.name);
    }

    // Show appropriate notifications
    if (replacedCustomizations.length > 0) {
      const replacementText = replacedCustomizations.map(r => `${r.old} â†’ ${r.new}`).join(', ');
      components.ui.showNotification(
        `Updated ${cartItem.name}: ${replacementText}`,
        "success"
      );
    }

    if (addedCustomizations.length > 0) {
      components.ui.showNotification(
        `Added ${addedCustomizations.join(', ')} to ${cartItem.name}`,
        "success"
      );
    }

    if (alreadyExists.length > 0) {
      components.ui.showNotification(
        `${alreadyExists.join(', ')} already on ${cartItem.name}`,
        "info"
      );
    }

    const orderSummary = components.cart.getOrderSummary();

    // Sending cart item to the API for synchronization
    const tokens = await auth.getTokens();
    const payload = {
      roomNumber: utils.storage.getData('guestRoomNumber'),
      loyaltyId: input.loyaltyId || "null",
      cartSummary: JSON.stringify(orderSummary).replace(/"/g, '\\"')
    }
    console.log("Tool::Script::"+toolName+" syncing with API:", JSON.stringify(payload, null, 2));

    // Fire-and-forget with error handling
    if (tokens.idToken && globals.cartAPIURL) {
      axios.post(globals.cartAPIURL, payload, {
        headers: {
          "Authorization": `${tokens.idToken}`,
          "Content-Type": "application/json"
        }
      }).then(response => {
        console.log("Tool::Script::"+toolName+" cart synced successfully:", response.data);
      }).catch(syncError => {
        console.error("Tool::Script::"+toolName+" cart sync error:", syncError);
        console.error("Tool::Script::"+toolName+" error details:", syncError.response?.data);
        // Don't fail the whole operation if sync fails
      });
    } else {
      console.log("Tool::Script::"+toolName+" missing tokens or cartAPIURL");
    }

    return JSON.stringify({ 
      success: true,
      result: "success",
      message: [
        ...replacedCustomizations.map(r => `Replaced ${r.old} with ${r.new}`),
        ...addedCustomizations.map(c => `Added ${c}`),
        ...alreadyExists.map(c => `${c} already existed`)
      ].join('; ') || "No changes made",
      addedCustomizations: addedCustomizations,
      replacedCustomizations: replacedCustomizations,
      alreadyExisted: alreadyExists,
      itemModified: item,
      cartSummary: orderSummary
    });

  } catch (error) {
    console.error("Tool::Script::"+toolName+" error:", error);
    components.ui.showNotification("Failed to add customization", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

