async function execute({...args}) {
  const { input, components, toolName, utils, auth, axios, globals } = args;
  console.log("Tool::Script::" + toolName + " executed with input:", JSON.stringify(input, null, 2));

  try {
    // Check if using existing format (cartItemId)
    if (input.items && Array.isArray(input.items) && input.items.length > 0 && input.items[0].cartItemId) {
      console.log("Tool::Script::" + toolName + " using EXISTING format (cartItemId)");
      return await executeExistingLogic(input, components, toolName, auth, axios, globals, utils);
    }

    // Check if using new smart format (itemDescription)
    if (input.itemsToRemove && Array.isArray(input.itemsToRemove) && input.itemsToRemove.length > 0) {
      console.log("Tool::Script::" + toolName + " using NEW smart format (itemDescription)");
      return await executeSmartLogic(input, components, toolName, auth, axios, globals, utils);
    }

    throw new Error("Invalid input format. Use either 'items' with 'cartItemId' or 'itemsToRemove' with 'itemDescription'");

  } catch (error) {
    console.error("Tool::Script::" + toolName + " error:", error);
    components.ui.showNotification("Failed to remove items from cart", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

// Existing logic (unchanged)
async function executeExistingLogic(input, components, toolName, auth, axios, globals, utils) {
  console.log("Tool::Script::" + toolName + " EXISTING LOGIC: processing", input.items.length, "items");
  const processedItems = [];

  // Initialize cartResult at the top
  let cartResult = null;

  for (const element of input.items) {
    console.log("Tool::Script::" + toolName + " processing item:", JSON.stringify(element, null, 2));

    const cartItem = components.cart.getCartItem(element.cartItemId);
    console.log("Tool::Script::" + toolName + " getCartItem result:", JSON.stringify(cartItem, null, 2));

    if (!cartItem) {
      console.error("Tool::Script::" + toolName + " ERROR: Cart item with ID", element.cartItemId, "not found");
      components.ui.showNotification(`Cart item with ID "${element.cartItemId}" not found in cart`, "error");
      continue;
    }

    const newQuantity = cartItem.quantity - element.quantity;
    console.log("Tool::Script::" + toolName + " quantity calculation:", cartItem.quantity, "-", element.quantity, "=", newQuantity);

    if (newQuantity < 0) {
      console.error("Tool::Script::" + toolName + " ERROR: Cannot remove", element.quantity, "items. Only", cartItem.quantity, "available");
      components.ui.showNotification(`Cannot remove ${element.quantity} items. Only ${cartItem.quantity} available in cart`, "error");
      continue;
    }

    console.log("Tool::Script::" + toolName + " calling components.cart.updateQuantity with cartItemId:", element.cartItemId, "newQuantity:", newQuantity);
    const updateResult = components.cart.updateQuantity(element.cartItemId, newQuantity);
    cartResult = { summary: updateResult };
    console.log("Tool::Script::" + toolName + " updateQuantity result:", JSON.stringify(updateResult, null, 2));

    processedItems.push({
      cartItemId: element.cartItemId,
      itemName: cartItem.name,
      removedQuantity: element.quantity,
      newQuantity: newQuantity,
      action: newQuantity === 0 ? 'removed' : 'updated'
    });

    if (newQuantity === 0) {
      console.log("Tool::Script::" + toolName + " item completely removed");
      components.ui.showNotification(`Removed all ${cartItem.name} from cart`, "success");
    } else {
      console.log("Tool::Script::" + toolName + " item quantity updated");
      components.ui.showNotification(`Removed ${element.quantity}x ${cartItem.name} from cart (${newQuantity} remaining)`, "success");
    }
  }

  const finalCartSummary = components.cart.getOrderSummary();
  console.log("Tool::Script::" + toolName + " final cart summary:", JSON.stringify(finalCartSummary, null, 2));

  // Ensure cartResult is always set
  if (!cartResult) {
    cartResult = { summary: finalCartSummary };
  }

  // Sending cart item to the API for synchronization
  const tokens = await auth.getTokens();
  const payload = {
    roomNumber: utils.storage.getData('guestRoomNumber'),
    loyaltyId: input.loyaltyId || "null",
    cartSummary: JSON.stringify(cartResult.summary).replace(/"/g, '\\"')
  }
  console.log("Tool::Script::"+toolName+" syncing with API:", JSON.stringify(payload, null, 2));

  // Fire-and-forget with error handling
  if (tokens.idToken && globals.cartAPIURL) {
    axios.post(globals.cartAPIURL, payload, {
      headers: {
        "Authorization": `${tokens.idToken}`,
        "Content-Type": "application/json"
      }
    }).then(response => {
      console.log("Tool::Script::"+toolName+" cart synced successfully:", response.data);
    }).catch(syncError => {
      console.error("Tool::Script::"+toolName+" cart sync error:", syncError);
      console.error("Tool::Script::"+toolName+" error details:", syncError.response?.data);
      // Don't fail the whole operation if sync fails
    });
  } else {
    console.log("Tool::Script::"+toolName+" missing tokens or cartAPIURL");
  }

  const success = processedItems.length > 0;
  const result = JSON.stringify({
    success: success,
    processedItems: processedItems,
    cartSummary: cartResult.summary
  }); 

  console.log("Tool::Script::" + toolName + " EXISTING LOGIC executed with output:", result);
  return result;
}

// SIMPLIFIED Smart Logic - Focus on the core issue
async function executeSmartLogic(input, components, toolName, auth, axios, globals, utils) {
  console.log("Tool::Script::" + toolName + " SMART LOGIC: processing", input.itemsToRemove.length, "item descriptions");

  // Initialize cartResult at the top
  let cartResult = null;

  const currentCart = components.cart.getOrderSummary();
  console.log("Tool::Script::" + toolName + " current cart:", JSON.stringify(currentCart, null, 2));

  if (!currentCart.items || currentCart.items.length === 0) {
    components.ui.showNotification("Your cart is empty", "info");
    return JSON.stringify({
      success: false,
      error: "Cart is empty",
      cartSummary: currentCart
    });
  }

  const processedItems = [];
  const clarificationNeeded = [];

  for (const element of input.itemsToRemove) {
    const itemDescription = element.itemDescription?.toLowerCase().trim();
    const requestedQuantity = element.quantity || 1;

    if (!itemDescription) {
      console.log("Tool::Script::" + toolName + " skipping empty item description");
      continue;
    }

    console.log("Tool::Script::" + toolName + " looking for:", itemDescription);

    // SIMPLE MATCHING LOGIC - Focus on the core issue
    const matches = currentCart.items.filter(cartItem => {
      const itemName = cartItem.name.toLowerCase();
      console.log("Tool::Script::" + toolName + " checking:", cartItem.name, "against:", itemDescription);

      // RULE 1: If customer mentions "with [customization]", only match items that have that customization
      if (itemDescription.includes('with ')) {
        // Extract what they want: "milkshake with whipped cream" -> "whipped cream"
        const wantedCustomization = itemDescription.replace(/.*with\s+/, '').trim();

        // Only match items that have customizations (~)
        if (itemName.includes('~')) {
          const itemCustomizations = itemName.split('~')[1];
          // Clean up pricing and check if it contains what they want
          const cleanCustomizations = itemCustomizations
            .replace(/\(\+\$[\d.]+\)/g, '')
            .replace(/\(\-\$[\d.]+\)/g, '')
            .trim()
            .toLowerCase();

          // Check if the customization matches
          if (cleanCustomizations.includes(wantedCustomization)) {
            console.log("Tool::Script::" + toolName + " CUSTOMIZATION match:", cartItem.name);
            return true;
          }
        }
        // If they want customization but this item has none, don't match
        return false;
      }

      // RULE 2: If customer says just the base item name, match all variants
      const baseItemName = itemName.split('~')[0].trim();
      if (baseItemName.toLowerCase().includes(itemDescription) || itemDescription.includes(baseItemName.toLowerCase())) {
        console.log("Tool::Script::" + toolName + " BASE match:", cartItem.name);
        return true;
      }

      return false;
    });

    console.log("Tool::Script::" + toolName + " found", matches.length, "matches for:", itemDescription);
    matches.forEach(match => console.log("Tool::Script::" + toolName + " - Match:", match.name));

    if (matches.length === 0) {
      const availableItems = currentCart.items.map(item => item.name).join(', ');
      console.log("Tool::Script::" + toolName + " no matches found");

      return JSON.stringify({
        success: false,
        error: `"${element.itemDescription}" not found in your cart`,
        availableItems: currentCart.items.map(item => ({
          id: item.id,
          name: item.name,
          quantity: item.quantity
        })),
        message: `I don't see "${element.itemDescription}" in your cart. Available items: ${availableItems}`,
        cartSummary: currentCart
      });

    } else if (matches.length === 1) {
      // Single match - remove directly
      const cartItem = matches[0];
      console.log("Tool::Script::" + toolName + " single match found:", cartItem.name);

      if (requestedQuantity > cartItem.quantity) {
        components.ui.showNotification(`Cannot remove ${requestedQuantity}x ${cartItem.name}. Only ${cartItem.quantity} available`, "error");
        continue;
      }

      const newQuantity = cartItem.quantity - requestedQuantity;
      console.log("Tool::Script::" + toolName + " removing", requestedQuantity, "from", cartItem.quantity, "=", newQuantity);

      const updateResult = components.cart.updateQuantity(cartItem.id, newQuantity);
      cartResult = { summary: updateResult };
      console.log("Tool::Script::" + toolName + " updateQuantity result:", JSON.stringify(updateResult, null, 2));

      processedItems.push({
        cartItemId: cartItem.id,
        itemName: cartItem.name,
        removedQuantity: requestedQuantity,
        newQuantity: newQuantity,
        action: newQuantity === 0 ? 'removed' : 'updated'
      });

      if (newQuantity === 0) {
        components.ui.showNotification(`Removed all ${cartItem.name} from cart`, "success");
      } else {
        components.ui.showNotification(`Removed ${requestedQuantity}x ${cartItem.name} from cart (${newQuantity} remaining)`, "success");
      }

    } else {
      // Multiple matches - need clarification
      console.log("Tool::Script::" + toolName + " multiple matches found, need clarification");

      clarificationNeeded.push({
        requestedItem: element.itemDescription,
        matches: matches.map(item => ({
          id: item.id,
          name: item.name,
          quantity: item.quantity,
          price: item.totalPrice
        }))
      });
    }
  }

  // Handle clarification - Simple and clear
  if (clarificationNeeded.length > 0) {
    const clarificationItem = clarificationNeeded[0];

    // Create clear descriptions - SIMPLE approach
    const matchNames = clarificationItem.matches.map((match, index) => {
      // Simple description with quantity for clarity
      const hasCustomizations = match.name.includes('~') || match.name.includes('with') || match.name.includes('+');

      if (hasCustomizations) {
        return `${match.name} (${match.quantity} qty)`;
      } else {
        return `${match.name} (${match.quantity} qty, regular)`;
      }
    }).join(' or ');

    return JSON.stringify({
      success: false,
      requiresClarification: true,
      requestedItem: clarificationItem.requestedItem,
      matches: clarificationItem.matches,
      message: `I found ${clarificationItem.matches.length} items matching "${clarificationItem.requestedItem}": ${matchNames}. Which one would you like me to remove?`,
      cartSummary: currentCart
    });
  }

  const finalCartSummary = components.cart.getOrderSummary();
  console.log("Tool::Script::" + toolName + " final cart summary:", JSON.stringify(finalCartSummary, null, 2));

  // Ensure cartResult is always set
  if (!cartResult) {
    cartResult = { summary: finalCartSummary };
  }

  // Sending cart item to the API for synchronization
  const tokens = await auth.getTokens();
  const payload = {
    roomNumber: utils.storage.getData('guestRoomNumber'),
    loyaltyId: input.loyaltyId || "null",
    cartSummary: JSON.stringify(cartResult.summary).replace(/"/g, '\\"')
  }
  console.log("Tool::Script::"+toolName+" syncing with API:", JSON.stringify(payload, null, 2));

  // Fire-and-forget with error handling
  if (tokens.idToken && globals.cartAPIURL) {
    axios.post(globals.cartAPIURL, payload, {
      headers: {
        "Authorization": `${tokens.idToken}`,
        "Content-Type": "application/json"
      }
    }).then(response => {
      console.log("Tool::Script::"+toolName+" cart synced successfully:", response.data);
    }).catch(syncError => {
      console.error("Tool::Script::"+toolName+" cart sync error:", syncError);
      console.error("Tool::Script::"+toolName+" error details:", syncError.response?.data);
      // Don't fail the whole operation if sync fails
    });
  } else {
    console.log("Tool::Script::"+toolName+" missing tokens or cartAPIURL");
  }

  const result = JSON.stringify({
    success: true,
    processedItems: processedItems,
    cartSummary: cartResult.summary,
    method: "smart_matching"
  });

  console.log("Tool::Script::" + toolName + " SMART LOGIC executed with output:", result);
  return result;
}

