tools.script:
async function execute({...args}) {
  const { toolName, components, axios, auth, globals, utils } = args;
  console.log("Tool::Script::" + toolName + " executed with args:", args);

  try {
    // Get order summary and chat messages
    const orderSummary = components.cart.getOrderSummary();
    const chatMessages = components.chat.getMessagesForStorage();

    console.log("Tool::Script::" + toolName + " orderSummary", orderSummary);
    console.log("Tool::Script::" + toolName + " chatMessages", chatMessages);

    // Check if room number is stored in session
    const storedRoomNumber = utils.storage.getData('guestRoomNumber');
    if (!storedRoomNumber) {
      components.ui.showNotification("Room number required", "warning");
      return JSON.stringify({
        success: false,
        error: "Room number required",
        message: "Please provide your room number first."
      });
    }

    // Get authentication tokens
    const tokens = await auth.getTokens();
    if (!tokens.idToken) {
      throw new Error("No access token available");
    }

    console.log("Tool::Script::"+ toolName +" idToken:", tokens.idToken);

    // Simple housekeeping detection by name
    const housekeepingKeywords = ['towel', 'linen', 'cleaning', 'housekeeping', 'laundry', 'pressing'];
    const housekeepingItems = orderSummary.items.filter(item => {
      const itemName = (item.name || '').toLowerCase();
      return housekeepingKeywords.some(keyword => itemName.includes(keyword));
    });
    const nonHousekeepingItems = orderSummary.items.filter(item => {
      const itemName = (item.name || '').toLowerCase();
      return !housekeepingKeywords.some(keyword => itemName.includes(keyword));
    });

    console.log("Tool::Script::" + toolName + " housekeeping items:", housekeepingItems.length);
    console.log("Tool::Script::" + toolName + " non-housekeeping items:", nonHousekeepingItems.length);

    let orderId = null;

    // Step 1: Submit non-housekeeping items to Order Table (if any)
    if (nonHousekeepingItems.length > 0) {
      const orderData = {
        customerId: "hotel-guest",
        total: orderSummary.total,
        items: JSON.stringify(nonHousekeepingItems).replace(/"/g, '\\"'),
        roomNumber: storedRoomNumber
      };

      console.log("Tool::Script::" + toolName + " sending order data:", orderData);

      const orderResponse = await axios.post(
        globals.orderAPIURL,
        orderData,
        {
          headers: {
            "Authorization": `${tokens.idToken}`,
            "Content-Type": "application/json"
          }
        }
      );

      console.log("Tool::Script::" + toolName + " order API response:", orderResponse.data);
      orderId = orderResponse.data.orderId;
    } else {
      // Generate orderId for housekeeping-only requests
      orderId = `HK-${Date.now()}`;
    }

    // Step 2: Submit housekeeping items to Housekeeping Table (if any)
    let housekeepingSubmitted = 0;
    if (housekeepingItems.length > 0) {
      console.log("Tool::Script::" + toolName + " submitting housekeeping items");

      try {
        const housekeepingData = {
          requestId: orderId,
          serviceType: "housekeeping",
          serviceName: housekeepingItems.map(item => item.name).join(", "),
          roomNumber: storedRoomNumber,
          quantity: housekeepingItems.reduce((sum, item) => sum + (item.quantity || 1), 0),
          urgency: "standard",
          priority: "normal",
          estimatedTime: "30 minutes",
          preferredTime: "ASAP",
          specialInstructions: housekeepingItems
            .map(item => {
              let instructions = "";

              // Handle different customization formats
              if (item.customizations) {
                if (Array.isArray(item.customizations)) {
                  instructions = item.customizations.map(c => c.name || c).join(", ");
                } else if (typeof item.customizations === 'string') {
                  instructions = item.customizations;
                } else if (typeof item.customizations === 'object') {
                  instructions = JSON.stringify(item.customizations);
                }
              } else if (item.specialInstructions) {
                instructions = item.specialInstructions;
              }

              return instructions ? `${item.name} - ${instructions}` : "";
            })
            .filter(instruction => instruction)
            .join(", "),
          requestTime: new Date().toISOString(),
          status: "pending"
        };

        await axios.post(
          globals.housekeepingAPIURL,
          housekeepingData,
          {
            headers: {
              "Authorization": `${tokens.idToken}`,
              "Content-Type": "application/json"
            }
          }
        );

        housekeepingSubmitted = housekeepingItems.length;
      } catch (housekeepingError) {
        console.error("Tool::Script::" + toolName + " housekeeping error:", housekeepingError);
      }
    }

    // Step 3: Save all chat messages in one batch request
    if (chatMessages.length > 0) {
      console.log("Tool::Script::" + toolName + " saving chat history batch for orderId:", orderId);

      const messagesForBatch = chatMessages.map(message => ({
        orderId: orderId,
        timestamp: message.createdAt,
        role: message.role,
        message: message.message,
        messageId: message.messageId
      }));

      const chatBatchData = {
        messages: messagesForBatch
      };

      console.log("Tool::Script::" + toolName + " sending chat batch data:", chatBatchData);

      const chatResponse = await axios.post(
        globals.chatAPIURL,
        chatBatchData,
        {
          headers: {
            "Authorization": `${tokens.idToken}`,
            "Content-Type": "application/json"
          }
        }
      );

      console.log("Tool::Script::" + toolName + " chat batch API response:", chatResponse.data);
    }

    // Step 4: Clear cart and show success
    components.cart.clearCart();

    // Step 5: Sync empty cart to DynamoDB
    const emptyCartSummary = {
      subtotal: 0,
      tax: 0,
      total: 0,
      items: [],
      totalQuantity: 0
    };
    const cartResult = { summary: emptyCartSummary };
    const payload = {
      roomNumber: storedRoomNumber,
      loyaltyId: "null",
      cartSummary: JSON.stringify(cartResult.summary).replace(/"/g, '\\"')
    }
    console.log("Tool::Script::"+toolName+" syncing empty cart with API:", JSON.stringify(payload, null, 2));

    // Fire-and-forget sync to clear backend cart
    if (tokens.idToken && globals.cartAPIURL) {
      axios.post(globals.cartAPIURL, payload, {
        headers: {
          "Authorization": `${tokens.idToken}`,
          "Content-Type": "application/json"
        }
      }).then(response => {
        console.log("Tool::Script::"+toolName+" empty cart synced successfully:", response.data);
      }).catch(syncError => {
        console.error("Tool::Script::"+toolName+" cart clear sync error:", syncError);
        // Don't fail the whole operation if sync fails
      });
    } else {
      console.log("Tool::Script::"+toolName+" missing tokens or cartAPIURL for cart clear sync");
    }

    // Create appropriate success message
    let successMessage = "";
    let confirmationMessage = "";

    if (nonHousekeepingItems.length > 0 && housekeepingItems.length > 0) {
      successMessage = `Order submitted! Food sent to kitchen, ${housekeepingSubmitted} housekeeping requests sent to staff.`;
      confirmationMessage = `Order #${orderId} - Food: $${orderSummary.total.toFixed(2)}, Housekeeping: ${housekeepingSubmitted} requests`;
    } else if (nonHousekeepingItems.length > 0) {
      successMessage = "Order submitted successfully! Your food and drinks have been sent to the kitchen.";
      confirmationMessage = `Order #${orderId} has been sent to the kitchen. Total: $${orderSummary.total.toFixed(2)}`;
    } else if (housekeepingItems.length > 0) {
      successMessage = `Housekeeping requests submitted! ${housekeepingSubmitted} requests sent to staff.`;
      confirmationMessage = `Request #${orderId} - ${housekeepingSubmitted} housekeeping services requested`;
    }

    components.ui.showNotification(successMessage, "success");
    components.chat.addMessage(confirmationMessage, "system");

    return JSON.stringify({
      success: true,
      result: "success",
      message: successMessage,
      orderId: orderId,
      orderSummary: orderSummary,
      housekeepingSubmitted: housekeepingSubmitted,
      chatMessagesSaved: chatMessages.length
    });

  } catch (error) {
    console.error("Tool::Script::" + toolName + " error:", error);

    components.ui.showNotification(
      "Failed to submit order. Please try again.",
      "error"
    );

    return JSON.stringify({
      success: false,
      error: error.message,
      message: "Failed to submit order. Please try again."
    });
  }
}

