inputSchema.json:
{
  "type": "object",
  "description": "Validates menu item availability and returns exact menu ID or suggestions",
  "properties": {
    "requestedItem": {
      "type": "string",
      "description": "The item name or description that the customer requested (e.g., 'lemon milkshake', 'cheese burger', 'buffalo wings')"
    }
  },
  "required": [
    "requestedItem"
  ]
}

tools.script:
async function execute({...args}) {
  const { input, components, toolName } = args;
  console.log("Tool::Script::"+toolName+" executed with input:", JSON.stringify(input, null, 2));

  try {
    if (!input.requestedItem || input.requestedItem.trim() === '') {
      return JSON.stringify({
        success: false,
        error: "No item specified for validation"
      });
    }

    const requestedItem = input.requestedItem.toLowerCase().trim();
    console.log("Tool::Script::"+toolName+" validating item:", requestedItem);

    // Get all available menu categories
    const allCategories = components.menu.getAllCategories();
    let allMenuItems = [];

    // Collect all menu items from all categories
    allCategories.forEach(category => {
      const categoryItems = components.menu.getMenuItems(category.id);
      if (categoryItems && categoryItems.length > 0) {
        allMenuItems = allMenuItems.concat(categoryItems);
      }
    });

    // Remove duplicates based on item ID
    allMenuItems = allMenuItems.filter((item, index, self) => 
      index === self.findIndex(i => i.id === item.id)
    );

    console.log("Tool::Script::"+toolName+" searching among", allMenuItems.length, "menu items");

    // 1. EXACT NAME MATCH (case insensitive)
    const exactMatch = allMenuItems.find(item => 
      item.name.toLowerCase() === requestedItem
    );

    if (exactMatch) {
      console.log("Tool::Script::"+toolName+" EXACT MATCH found:", exactMatch.name);
      return JSON.stringify({
        success: true,
        matchType: "exact",
        validatedItem: {
          id: exactMatch.id,
          name: exactMatch.name,
          price: exactMatch.price,
          category: exactMatch.category,
          available: exactMatch.available
        },
        message: `Found exact match: ${exactMatch.name}`,
        instruction: `Use itemId: "${exactMatch.id}" for AddToCart`
      });
    }

    // 2. PARTIAL MATCHES (contains search term or word matches)
    const partialMatches = allMenuItems.filter(item => {
      const itemName = item.name.toLowerCase();

      // Direct substring match
      if (itemName.includes(requestedItem) || requestedItem.includes(itemName)) {
        return true;
      }

      // Word-by-word matching
      const requestedWords = requestedItem.split(/[\s_-]+/).filter(word => word.length > 2);
      const itemWords = itemName.split(/[\s_-]+/).filter(word => word.length > 2);

      // Check if any requested word matches any item word
      return requestedWords.some(reqWord => 
        itemWords.some(itemWord => 
          itemWord.includes(reqWord) || reqWord.includes(itemWord)
        )
      );
    });

    if (partialMatches.length > 0) {
      console.log("Tool::Script::"+toolName+" PARTIAL MATCHES found:", partialMatches.length);

      // Sort by relevance (prioritize items that start with search term, then by length)
      partialMatches.sort((a, b) => {
        const aName = a.name.toLowerCase();
        const bName = b.name.toLowerCase();

        // Prioritize items that start with the search term
        const aStarts = aName.startsWith(requestedItem);
        const bStarts = bName.startsWith(requestedItem);
        if (aStarts && !bStarts) return -1;
        if (!aStarts && bStarts) return 1;

        // Then prioritize items that contain the search term early
        const aIndex = aName.indexOf(requestedItem);
        const bIndex = bName.indexOf(requestedItem);
        if (aIndex !== -1 && bIndex !== -1) {
          if (aIndex !== bIndex) return aIndex - bIndex;
        }

        // Finally by length (shorter = more relevant)
        return aName.length - bName.length;
      });

      return JSON.stringify({
        success: false,
        matchType: "partial",
        error: `"${input.requestedItem}" is not available in our menu`,
        suggestions: partialMatches.slice(0, 3).map(item => ({
          id: item.id,
          name: item.name,
          price: item.price
        })),
        message: `Did you mean: ${partialMatches.slice(0, 3).map(item => item.name).join(', ')}?`,
        instruction: "Please confirm which item you'd like, then use the exact itemId for AddToCart"
      });
    }

    // 3. CATEGORY-BASED SUGGESTIONS (Dynamic - no hardcoding)
    // Extract potential category hints from the requested item
    const requestedWords = requestedItem.split(/[\s_-]+/).filter(word => word.length > 2);
    let categoryMatches = [];

    // Check if any word in the request matches any category name
    allCategories.forEach(category => {
      const categoryName = category.name.toLowerCase();
      const categoryDisplayName = category.displayName.toLowerCase();

      const matchesCategory = requestedWords.some(word => 
        categoryName.includes(word) || 
        categoryDisplayName.includes(word) ||
        word.includes(categoryName) ||
        word.includes(categoryDisplayName)
      );

      if (matchesCategory) {
        const categoryItems = allMenuItems.filter(item => 
          item.category && item.category.some(cat => 
            cat.toLowerCase() === category.name.toLowerCase() ||
            cat.toLowerCase() === category.displayName.toLowerCase()
          )
        );

        if (categoryItems.length > 0) {
          categoryMatches = categoryMatches.concat(categoryItems);
        }
      }
    });

    // Remove duplicates from category matches
    categoryMatches = categoryMatches.filter((item, index, self) => 
      index === self.findIndex(i => i.id === item.id)
    );

    if (categoryMatches.length > 0) {
      console.log("Tool::Script::"+toolName+" CATEGORY MATCHES found:", categoryMatches.length);
      return JSON.stringify({
        success: false,
        matchType: "category",
        error: `"${input.requestedItem}" is not available in our menu`,
        suggestions: categoryMatches.slice(0, 5).map(item => ({
          id: item.id,
          name: item.name,
          price: item.price
        })),
        message: `We don't have "${input.requestedItem}", but here are some options that might interest you: ${categoryMatches.slice(0, 3).map(item => item.name).join(', ')}`,
        instruction: "Please choose from these options, then use the exact itemId for AddToCart"
      });
    }

    // 4. NO MATCHES FOUND
    console.log("Tool::Script::"+toolName+" NO MATCHES found for:", requestedItem);
    return JSON.stringify({
      success: false,
      matchType: "none",
      error: `"${input.requestedItem}" is not available in our menu`,
      message: "Please choose from our available menu items or ask me to show you our menu categories",
      instruction: "Use GetMenuItems to see all available options, then use exact itemId for AddToCart",
      availableCategories: allCategories.map(cat => cat.displayName)
    });

  } catch (error) {
    console.error("Tool::Script::"+toolName+" error:", error);
    return JSON.stringify({
      success: false,
      error: `Error validating menu item: ${error.message}`
    });
  }
}

inputSchema.json:
{
  "type": "object",
  "description": "Replace a specific item within an existing combo with a different menu item",
  "properties": {
    "comboCartItemId": {
      "type": "string",
      "description": "Cart ID of the combo to modify"
    },
    "oldItemName": {
      "type": "string",
      "description": "Name of the item to replace (e.g., 'cola', 'fries')"
    },
    "newItemId": {
      "type": "string",
      "description": "Menu ID of the replacement item"
    }
  },
  "required": [
    "comboCartItemId",
    "oldItemName",
    "newItemId"
  ]
}

tools.script:
async function execute({...args}) {
  const { input, components, toolName, utils } = args;
  console.log("Tool::Script::" + toolName + " executed with input:", JSON.stringify(input, null, 2));

  try {
    // Get the combo from cart
    const comboItem = components.cart.getCartItem(input.comboCartItemId);
    if (!comboItem) {
      return JSON.stringify({
        success: false,
        error: "Combo not found in cart"
      });
    }

    // Get replacement item from menu
    const newItem = components.menu.getItemById(input.newItemId);
    if (!newItem) {
      return JSON.stringify({
        success: false,
        error: "Replacement item not found in menu"
      });
    }

    // Use existing comboItems if available, otherwise get from menu
    let currentComboItems;
    if (comboItem.comboItems && comboItem.comboItems.length > 0) {
      // Use existing modified combo items
      currentComboItems = [...comboItem.comboItems];
      console.log("Tool::Script::" + toolName + " using existing combo items:", currentComboItems);
    } else {
      // Get original combo definition from menu
      const comboMenuData = components.menu.getItemById(comboItem.menuItemId);
      if (!comboMenuData || !comboMenuData.items) {
        return JSON.stringify({
          success: false,
          error: "Combo structure not found in menu"
        });
      }

      // Create initial combo items from menu data
      currentComboItems = comboMenuData.items.map(item => ({
        id: item.id,
        name: item.name,
        price: item.price,
        isReplacement: false
      }));
      console.log("Tool::Script::" + toolName + " created initial combo items from menu:", currentComboItems);
    }

    // Find the item to replace in current combo items
    const itemToReplaceIndex = currentComboItems.findIndex(item => 
      item.name.toLowerCase().includes(input.oldItemName.toLowerCase()) ||
      input.oldItemName.toLowerCase().includes(item.name.toLowerCase()) ||
      (item.originalName && item.originalName.toLowerCase().includes(input.oldItemName.toLowerCase()))
    );

    if (itemToReplaceIndex === -1) {
      return JSON.stringify({
        success: false,
        error: `Item "${input.oldItemName}" not found in ${comboItem.name}`,
        availableItems: currentComboItems.map(item => item.name)
      });
    }

    const itemToReplace = currentComboItems[itemToReplaceIndex];
    console.log("Tool::Script::" + toolName + " found item to replace:", itemToReplace);

    // Calculate price difference (use original price if this was already a replacement)
    const originalPrice = itemToReplace.originalPrice || itemToReplace.price;
    const priceDifference = newItem.price - originalPrice;

    // Replace the item in the combo items array
    currentComboItems[itemToReplaceIndex] = {
      id: newItem.id,
      name: newItem.name,
      price: newItem.price,
      isReplacement: true,
      originalName: itemToReplace.originalName || itemToReplace.name,
      originalPrice: originalPrice,
      priceDifference: priceDifference
    };

    console.log("Tool::Script::" + toolName + " updated combo items:", currentComboItems);

    // Calculate total price difference from all modifications
    const totalPriceDifference = currentComboItems.reduce((sum, item) => {
      return sum + (item.priceDifference || 0);
    }, 0);

    // Get original combo base price (without previous modifications)
    const comboMenuData = components.menu.getItemById(comboItem.menuItemId);
    const originalComboPrice = comboMenuData ? comboMenuData.price : comboItem.basePrice;
    const newComboPrice = originalComboPrice + totalPriceDifference;

    console.log("Tool::Script::" + toolName + " price calculation:", {
      originalComboPrice,
      totalPriceDifference,
      newComboPrice
    });

    // Update the combo item
    const updatedCombo = {
      ...comboItem,
      basePrice: newComboPrice,
      totalPrice: newComboPrice * comboItem.quantity,
      comboItems: currentComboItems
    };

    // Update the cart item
    components.cart.updateCartItem(input.comboCartItemId, updatedCombo);

    // Show notification
    const priceChangeText = priceDifference > 0 ? ` (+$${priceDifference.toFixed(2)})` : 
                           priceDifference < 0 ? ` (-$${Math.abs(priceDifference).toFixed(2)})` : '';

    components.ui.showNotification(
      `Replaced ${itemToReplace.originalName || itemToReplace.name} with ${newItem.name}${priceChangeText}`,
      "success"
    );

    // Get updated cart summary
    const cartSummary = components.cart.getOrderSummary();

    return JSON.stringify({
      success: true,
      message: `Successfully replaced ${itemToReplace.originalName || itemToReplace.name} with ${newItem.name}`,
      comboItems: currentComboItems,
      priceDifference: priceDifference,
      totalPriceDifference: totalPriceDifference,
      cartSummary: cartSummary
    });

  } catch (error) {
    console.error("Tool::Script::" + toolName + " error:", error);
    components.ui.showNotification("Failed to replace combo item", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

inputSchema.json:
{
  "type": "object",
  "description": "Remove specific customizations from cart items using natural language description",
  "properties": {
    "customizationDescription": {
      "type": "string",
      "description": "Natural language description of customization to remove (e.g., 'extra cheese', 'no onions', 'whipped cream')"
    },
    "cartItemId": {
      "type": "string",
      "description": "Optional: specific cart item ID if customer specifies which item to modify"
    },
    "removeFromAllQuantities": {
      "type": "boolean",
      "description": "Optional: if item has quantity > 1, whether to remove customization from all quantities (true) or ask for clarification (false)",
      "default": false
    }
  },
  "required": [
    "customizationDescription"
  ]
}

tools.script:
async function execute({...args}) {
  const { input, components, toolName, auth, axios, globals, utils } = args;
  console.log("Tool::Script::" + toolName + " executed with input:", JSON.stringify(input, null, 2));

  try {
    // Get current cart
    const currentCart = components.cart.getOrderSummary();

    if (!currentCart.items || currentCart.items.length === 0) {
      components.ui.showNotification("Your cart is empty", "info");
      return JSON.stringify({
        success: false,
        error: "Cart is empty",
        cartSummary: currentCart
      });
    }

    const customizationDescription = input.customizationDescription.toLowerCase().trim();
    console.log("Tool::Script::" + toolName + " looking for customization:", customizationDescription);

    // Find items with matching customizations
    const matchingItems = [];

    for (const cartItem of currentCart.items) {
      if (cartItem.customizations && cartItem.customizations.length > 0) {
        // Find matching customizations in this item
        const matchingCustomizations = cartItem.customizations.filter(custom => {
          const customName = custom.name.toLowerCase();
          // Match by exact name or partial match
          return customName.includes(customizationDescription) || 
                 customizationDescription.includes(customName);
        });

        if (matchingCustomizations.length > 0) {
          matchingItems.push({
            cartItem: cartItem,
            matchingCustomizations: matchingCustomizations
          });
        }
      }
    }

    console.log("Tool::Script::" + toolName + " found", matchingItems.length, "items with matching customizations");

    // Handle different scenarios
    if (matchingItems.length === 0) {
      // Scenario 3: No items found with the customization
      const availableCustomizations = currentCart.items
        .flatMap(item => item.customizations || [])
        .map(custom => custom.name)
        .filter((name, index, arr) => arr.indexOf(name) === index); // Remove duplicates

      components.ui.showNotification(`Customization "${input.customizationDescription}" not found in cart`, "warning");

      return JSON.stringify({
        success: false,
        error: `"${input.customizationDescription}" customization not found in your cart`,
        availableCustomizations: availableCustomizations,
        message: availableCustomizations.length > 0 
          ? `Available customizations to remove: ${availableCustomizations.join(', ')}`
          : "No customizations found in your cart",
        cartSummary: currentCart
      });
    }

    // If specific cartItemId provided, filter to that item only
    if (input.cartItemId) {
      const specificItem = matchingItems.find(match => match.cartItem.id === input.cartItemId);
      if (!specificItem) {
        return JSON.stringify({
          success: false,
          error: `Customization "${input.customizationDescription}" not found in specified cart item`,
          cartSummary: currentCart
        });
      }
      matchingItems.length = 0;
      matchingItems.push(specificItem);
    }

    if (matchingItems.length > 1) {
      // Scenario 4: Multiple items found - ask for clarification
      components.ui.showNotification("Multiple items found with that customization", "info");

      return JSON.stringify({
        success: false,
        requiresClarification: true,
        error: `Found "${input.customizationDescription}" in multiple items`,
        matchingItems: matchingItems.map(match => ({
          cartItemId: match.cartItem.id,
          itemName: match.cartItem.name,
          quantity: match.cartItem.quantity,
          customizations: match.matchingCustomizations.map(c => c.name)
        })),
        message: `I found "${input.customizationDescription}" in ${matchingItems.length} items: ${matchingItems.map(m => m.cartItem.name).join(', ')}. Which item would you like me to remove it from?`,
        cartSummary: currentCart
      });
    }

    // Single item found - proceed with removal
    const targetMatch = matchingItems[0];
    const cartItem = targetMatch.cartItem;
    const customizationsToRemove = targetMatch.matchingCustomizations;

    console.log("Tool::Script::" + toolName + " removing from item:", cartItem.name);
    console.log("Tool::Script::" + toolName + " customizations to remove:", customizationsToRemove);

    // Handle quantity > 1 scenario
    if (cartItem.quantity > 1 && !input.removeFromAllQuantities) {
      components.ui.showNotification("Item has multiple quantities", "info");

      return JSON.stringify({
        success: false,
        requiresQuantityDecision: true,
        cartItem: {
          id: cartItem.id,
          name: cartItem.name,
          quantity: cartItem.quantity
        },
        customizationsToRemove: customizationsToRemove.map(c => c.name),
        message: `You have ${cartItem.quantity}x ${cartItem.name}. Do you want to remove "${input.customizationDescription}" from all ${cartItem.quantity} items, or just some of them?`,
        options: [
          "Remove from all quantities",
          "Remove from specific quantity (will split into separate items)"
        ],
        cartSummary: currentCart
      });
    }

    // Remove each matching customization
    const removedCustomizations = [];
    for (const customization of customizationsToRemove) {
      console.log("Tool::Script::" + toolName + " removing customization:", customization.name, "from item:", cartItem.id);

      // Use the exposed removeCustomizationFromCartItem function
      components.cart.removeCustomizationFromCartItem(cartItem.id, customization.id);
      removedCustomizations.push(customization.name);
    }

    // Show success notification
    const customizationText = removedCustomizations.join(', ');
    components.ui.showNotification(
      `Removed ${customizationText} from ${cartItem.name}`,
      "success"
    );

    // Get updated cart summary
    const finalCartSummary = components.cart.getOrderSummary();

    // Sending cart item to the API for synchronization
    const tokens = await auth.getTokens();
    const payload = {
      roomNumber: utils.storage.getData('guestRoomNumber'),
      loyaltyId: input.loyaltyId || "null",
      cartSummary: JSON.stringify(finalCartSummary).replace(/"/g, '\\"')
    }
    console.log("Tool::Script::"+toolName+" syncing with API:", JSON.stringify(payload, null, 2));

    // Fire-and-forget with error handling
    if (tokens.idToken && globals.cartAPIURL) {
      axios.post(globals.cartAPIURL, payload, {
        headers: {
          "Authorization": `${tokens.idToken}`,
          "Content-Type": "application/json"
        }
      }).then(response => {
        console.log("Tool::Script::"+toolName+" cart synced successfully:", response.data);
      }).catch(syncError => {
        console.error("Tool::Script::"+toolName+" cart sync error:", syncError);
        console.error("Tool::Script::"+toolName+" error details:", syncError.response?.data);
        // Don't fail the whole operation if sync fails
      });
    } else {
      console.log("Tool::Script::"+toolName+" missing tokens or cartAPIURL");
    }

    return JSON.stringify({
      success: true,
      message: `Successfully removed ${customizationText} from ${cartItem.name}`,
      removedCustomizations: removedCustomizations,
      fromItem: {
        id: cartItem.id,
        name: cartItem.name,
        quantity: cartItem.quantity
      },
      cartSummary: finalCartSummary
    });

  } catch (error) {
    console.error("Tool::Script::" + toolName + " error:", error);
    components.ui.showNotification("Failed to remove customization", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}
