async function execute({...args}) {
  const { input, components, toolName } = args;
  console.log("Tool::Script::"+toolName+" executed with input:", JSON.stringify(input, null, 2));

  try {
    const items = input.items;
    const processedItems = [];
    let totalSavings = 0;

    for(const element of items) {
      const item = components.menu.getItemById(element.itemId);

      if (!item) {
        components.ui.showNotification(`Item "${element.itemId}" not found`, "error");
        continue;
      }

      const customizations = [];
      if (Array.isArray(element.customizations)) {
        for (const cRequested of element.customizations) {
          const foundCustomization = item.availableCustomizations.find(c => 
            cRequested.id === c.id
          );
          if (foundCustomization) {
            customizations.push(foundCustomization);
          }
        }
      }

      const currentCart = components.cart.getOrderSummary();
      const largePizzasInCart = currentCart.items.filter(cartItem => 
        cartItem.name.includes('Large') && cartItem.name.includes('Pizza')
      ).length;

      let familyDealApplied = false;
      if (item.name.includes('Large') && item.name.includes('Pizza') && largePizzasInCart >= 1) {
        familyDealApplied = true;
        totalSavings += item.price * 0.1;
      }

      const result = components.cart.addToCart(
        String(item.id),
        item.name,
        item.price,
        element.quantity || 1,
        customizations
      );

      const customizationText = customizations.length > 0 
        ? ` with ${customizations.map(c => c.name).join(', ')}`
        : '';

      let notificationText = `Added ${element.quantity || 1}x ${item.name}${customizationText}`;
      if (familyDealApplied) {
        notificationText += ' ðŸŽ‰ Family Deal Applied!';
      }

      components.ui.showNotification(notificationText, "success");

      processedItems.push({
        itemId: element.itemId,
        itemName: item.name,
        quantity: element.quantity || 1,
        customizations: customizations,
        familyDealApplied: familyDealApplied
      });
    }

    const cartSummary = components.cart.getOrderSummary();

    const result = {
      success: true,
      processedItems: processedItems,
      cartSummary: cartSummary
    };

    if (totalSavings > 0) {
      result.familyDeal = {
        applied: true,
        savings: totalSavings,
        message: `Family Deal: Save $${totalSavings.toFixed(2)} on multiple large pizzas!`
      };
    }

    return JSON.stringify(result);

  } catch (error) {
    console.error("Tool::Script::"+toolName+" error:", error);
    components.ui.showNotification("Failed to add item to cart", "error");
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}

