async function execute({...args}) {
  const { input, components, toolName } = args;
  console.log("Tool::Script::"+toolName+" showing category:", input.category);

  try {
    components.menu.showCategory(input.category);
    const items = components.menu.getMenuItems(input.category);

    components.ui.showNotification(
      `Showing ${items.length} items in ${input.category}`, 
      "info"
    );

    return JSON.stringify({
      success: true,
      category: input.category,
      itemCount: items.length,
      items: items.map(item => ({
        id: item.id,
        name: item.name,
        description: item.description,
        price: item.price,
        size: item.size || 'N/A'
      }))
    });

  } catch (error) {
    console.error("Tool::Script::"+toolName+" error:", error);
    return JSON.stringify({
      success: false,
      error: error.message
    });
  }
}
